        private void LimpiarVenta()
        {
            this.TextBoxPagar.Text = "";
            this.ButtonPagar.Enabled = false;
            this.ComboBoxAlmacen.Enabled = false;
            this.ComboBoxDireccion.Enabled = false;
            this.ComboBoxDescripcionDelProducto.Enabled = false;
            this.ComboBoxTalla.Enabled = false;
            this.NumericUpDownCantidad.Enabled = false;
            this.ButtonGenerarVenta.Enabled = true;
            this.ButtonCancelarVenta.Enabled = false;
            this.ButtonAnadirVenta.Enabled = false;
            this.ButtonDeshacer.Enabled = false;
            this.LabelError.Visible = false;
            this.TextBoxPagar.Enabled = false;
            this.LabelCantidadMax.Text = "Max 0";
            this.NumericUpDownCantidad.Maximum = 0;
            this.DataGridViewVentas.Rows.Clear();
            EstablecerValoresTotales();
        }

        private void EstablecerValoresTotales()
        {
            decimal SubTotal = 0;
            for (int i = 0; i < this.DataGridViewVentas.Rows.Count; i++)
                SubTotal += decimal.Parse(this.DataGridViewVentas.Rows[i].Cells[3].Value.ToString());
            decimal ITBMS = SubTotal * 0.07m;
            decimal Total = SubTotal + ITBMS;
            this.LabelSubTotalNum.Text = SubTotal.ToString("C2");
            this.LabelITBMSnum.Text = ITBMS.ToString("C2");
            this.LabelTotalNum.Text = Total.ToString("0.00");
        }

        private float Valor_paraNumericdecimal()
        {
            float valor = 0;
            for (int i = 0; i < DataGridViewVentas.Rows.Count; i++)
            {
                if (this.DataGridViewVentas.Rows[i].Cells[0].Value.ToString() == this.ComboBoxDescripcionDelProducto.Text &&
                    this.DataGridViewVentas.Rows[i].Cells[1].Value.ToString() == this.ComboBoxTalla.Text)
                    valor += float.Parse(this.DataGridViewVentas.Rows[i].Cells[2].Value.ToString());
            }
            return valor;
        }

